import React, { Component } from 'react'

export default class Button extends Component {
    render(){
      let { title, task } = this.props
      return(
        <button onClick = { task }>{ title }</button>
      )}
  }

  const btnStyle = {
    background: '#ff0000',
    color: '#fff',
    border: 'none',
    padding: '5px 9px',
    borderRadius: '50%',
    cursor: 'pointer',
    float: 'right'
}
