import { Component } from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import Button from './components/Button';

export default class App extends Component {

  constructor() {
    super();
    this.state = { 
      count: 0,
      fieldInput: '',
    };
  }

  countUp = () => {
    this.setState({
      count: this.state.count + 1
    });
  };

  countDown = () => {
    this.setState({
      count: this.state.count - 1
    });
  };

  onChange = (e) => {
    const temp = parseInt(e.target.value);
    if (isNaN(temp)){
      return;
    } else {
      this.setState({ [e.target.name]: temp });
    }
  };

  onSubmit = (e) => {
    e.preventDefault();
    this.setState({
      count: parseInt(this.state.fieldInput)
    });
    this.setState({ fieldInput: ''});
  };

  render() {
    let { count } = this.state;
    return (
      <div className = "App">
        <div className = "Counter">
          <h2>{ count }</h2>
        </div>
        <div className = "TextInputField">
          <form onSubmit={this.onSubmit}>
            <input 
              type="text" 
              name="fieldInput"
              style={inputBox}
              width="200%"
              placeholder="Set counter..."
              value={this.state.fieldInput}
              onChange={this.onChange}
            />
            <input
              type="submit"
              font-size="125%"
              value="Submit"
              className="btn"
              style={submitbutton}   
            />
          </form>
        </div>
        <div className = "Buttons">
          <Button
          title="Up" 
          task= { () => this.countUp() }
          />
          <div class="divider" />
          <Button 
          title="Down" 
          task= { () => this.countDown() }
          />
        </div>
      </div>
    ); 
  }
}

const inputBox = {
  background: 'AliceBlue',
  width: '20%',
  padding: '5px',
  margin: '8px 0',
}

const submitbutton = {
  background: 'MediumAquaMarine',
    color: 'Black',
    border: 'none',
    bordercolor: 'DarkGray',
    padding: '5px 12px',
    borderRadius: '2px',
    cursor: 'pointer',
}

ReactDOM.render(
  <App/>,
  document.getElementById('root')
);
