import React, { Component } from 'react'

export default class Button extends Component {
    render(){
      let { title, task } = this.props
      return(
        <button style={ btnStyle } onClick = { task }>{ title }</button>
      )}
  }

  const btnStyle = {
    background: 'coral',
    color: 'Black',
    border: 'solid',
    bordercolor: 'DarkGray',
    padding: '10px 12px',
    borderRadius: '0%',
    cursor: 'pointer',
}
